import { Emitter } from '../../../common/model/emitter';
/**
 * @hidden
 */
export class GraphCursor {
    constructor() {
        this.emitter = new Emitter();
        this.index_ = -1;
    }
    get index() {
        return this.index_;
    }
    set index(index) {
        const changed = this.index_ !== index;
        if (changed) {
            this.index_ = index;
            this.emitter.emit('change', {
                index: index,
                sender: this,
            });
        }
    }
}
